package main

import (
	"context"
	"net/http"
	"time"

	"github.com/prometheus/client_golang/prometheus"
	"github.com/prometheus/client_golang/prometheus/promhttp"
)

var (
	httpRequestsTotal = prometheus.NewCounterVec(
		prometheus.CounterOpts{
			Name: "http_requests_total",
			Help: "Total number of HTTP requests.",
		},
		[]string{"method", "user_agent"},
	)
	httpRequestDuration = prometheus.NewHistogramVec(
		prometheus.HistogramOpts{
			Name: "http_request_duration_seconds",
			Help: "Duration of HTTP requests.",
		},
		[]string{"method", "user_agent"},
	)
)

func init() {
	prometheus.MustRegister(httpRequestsTotal)
	prometheus.MustRegister(httpRequestDuration)
}

func middleware(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		userAgent := r.Header.Get("User-Agent")
		ctx := context.WithValue(r.Context(), "user_agent", userAgent)
		next.ServeHTTP(w, r.WithContext(ctx))
	})
}

func instrumentedHandler(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		method := r.Method
		userAgent, ok := r.Context().Value("user_agent").(string)
		if !ok {
			userAgent = "unknown"
		}
		start := time.Now()
		next.ServeHTTP(w, r)
		duration := time.Since(start).Seconds()
		httpRequestDuration.WithLabelValues(method, userAgent).Observe(duration)
		httpRequestsTotal.WithLabelValues(method, userAgent).Inc()
	})
}

func ExampleInstrumentHandlerWithLabelResolver() {
	http.Handle("/metrics", promhttp.Handler())

	exampleHandler := func(w http.ResponseWriter, r *http.Request) {
		w.Write([]byte("Hello, World!"))
	}

	instrumented := instrumentedHandler(http.HandlerFunc(exampleHandler))
	wrapped := middleware(instrumented)
	http.Handle("/example", wrapped)

	http.ListenAndServe(":3000", nil)
}

