	ls := webdav.NewMemLS()

	logger := func(r *http.Request, err error) {
		if err != nil {
			log.Printf("WebDAV request %s %s: %v", r.Method, r.URL, err)
		} else {
			log.Printf("WebDAV request %s %s processed successfully", r.Method, r.URL)
		}
	}

