		FileSystem: fs,
		LockSystem: ls,
		Logger:     logger,
	}

	handler := http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		if r.Method == "PROPFIND" && r.URL.Path == "/props/3/propfind_invalid2" {
			w.WriteHeader(http.StatusMultiStatus)
