func rootHandler(w http.ResponseWriter, r *http.Request) {
    span := trace.FromContext(r.Context())
    if span != nil {
        span.AddEvent("handled request")
        span.AddAttributes(
            trace.StringAttribute("user", "test"),
            trace.Int64Attribute("request.size", 100),
        )
    }

    client := &http.Client{
        Transport: trace.HTTPTransport(),
    }
    resp, err := client.Get("http://example.com")
    if err != nil {
        http.Error(w, "Error making external request", http.StatusInternalServerError)
        return
    }
    defer resp.Body.Close()
    body, _ := ioutil.ReadAll(resp.Body)
    fmt.Fprintf(w, "hello world\nExternal response: %s", body)
}

func main() {
    trace.RegisterExporter(stdoutexporter.New())
    handler := http.HandlerFunc(rootHandler)
    http.ListenAndServe(":50030", plugin.Handler(handler))
}

