package main

import (
	"errors"
	"fmt"
	"net/http"

	"github.com/labstack/echo/v4"
	"github.com/yourpackage/tenhou"
)

type mjHandler struct {
	log                echo.Logger
	tenhouMessageReceiver *tenhou.MessageReceiver
	majsoulMessageQueue  chan []byte
}

func (h *mjHandler) analysisTenhou(c echo.Context) error {
	body, err := c.Request().Body().Bytes()
	if err != nil {
		h.log.Errorf("Failed to read body: %v", err)
		return c.NoContent(http.StatusInternalServerError)
	}
	if err := h.tenhouMessageReceiver.Process(body); err != nil {
		h.log.Errorf("Failed to process Tenhou message: %v", err)
		return c.NoContent(http.StatusInternalServerError)
	}
	return c.NoContent(http.StatusOK)
}

func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	body, err := c.Request().Body().Bytes()
	if err != nil {
		h.log.Errorf("Failed to read body: %v", err)
		return c.NoContent(http.StatusInternalServerError)
	}
	h.majsoulMessageQueue <- body
	return c.NoContent(http.StatusOK)
}

func runServer(isHTTPS bool, port int) error {
	e := echo.New()
	h := &mjHandler{
		log:                e.Logger(),
		tenhouMessageReceiver: &tenhou.MessageReceiver{},
		majsoulMessageQueue:  make(chan []byte, 100),
	}
	e.POST("/tenhou", h.analysisTenhou)
	e.POST("/majsoul", h.analysisMajsoul)

	addr := fmt.Sprintf(":%d", port)
	if isHTTPS {
		return e.StartTLS(addr, "cert.pem", "key.pem")
	}
	return e.Start(addr)
}
