package main

import (
	"fmt"
	"net/http"

	"github.com/labstack/echo/v4"
)

var (
	workflowKey  string
	builderKey   string
	secretsHit   int
	triggerHit   int
)

func startWorkflowServer(host string, port uint64) {
	e := echo.New()

	e.Use(func(next echo.HandlerFunc) echo.HandlerFunc {
		return func(c echo.Context) error {
			key := c.Request().Header.Peek("X-Workflow-Key")
			if string(key) != workflowKey {
				return c.String(http.StatusUnauthorized, "Unauthorized")
			}
			return next(c)
		}
	})

	e.GET("/secrets", func(c echo.Context) error {
		secretsHit++
		return c.String(http.StatusOK, "Secrets handled")
	})

	e.POST("/trigger", func(c echo.Context) error {
		triggerHit++
		return c.String(http.StatusOK, "Trigger handled")
	})

	e.Start(fmt.Sprintf("%s:%d", host, port))
}
