package main

import (
	"github.com/gofiber/fiber/v2"
	"github.com/alibaba/sentinel-golang/api"
	"github.com/alibaba/sentinel-golang/core/flow"
	"github.com/alibaba/sentinel-golang/pkg"
)

func main() {
	app := fiber.New()

	// Initialize Sentinel
	pkg.Init()

	// Define flow rule
	rule := flow.NewFlowRule("http_server").
		SetCount(10).
		SetIntervalInSecond(1).
		SetControlBehavior(flow.Reject)

	flow.LoadRules([]*flow.FlowRule{rule})

	// Rate limiting middleware
	app.Use(func(c *fiber.Ctx) error {
		// Extract client IP from headers
		ip := c.Get("X-Forwarded-For")
		if ip == "" {
			ip = c.IP()
		}

		// Acquire token with IP as resource key
		token, err := api.Acquire("http_server", ip)
		if err != nil {
			return c.Status(429).JSON(fiber.Map{
				"error":   "Too many requests",
				"message": "Rate limit exceeded",
			})
		}
		defer token.Release()

		return c.Next()
	})

	// Test endpoint
	app.Get("/test", func(c *fiber.Ctx) error {
		return c.SendString("Request allowed")
	})

	app.Listen(":8080")
}
