func SetupServer() {
	r := gin.Default()
	cache := cache.New(1*time.Minute, 10*time.Second)
	r.GET("/cache_ping", func(c *gin.Context) {
		if val, found := cache.Get("cache_ping"); found {
			c.String(http.StatusOK, val.(string))
		} else {
			timestamp := time.Now().Unix()
			response := fmt.Sprintf("pong %d", timestamp)
			cache.Set("cache_ping", response, cache.DefaultExpiration)
			c.String(http.StatusOK, response)
		}
	})
	r.Run(":8080")
}

