import (
	"bytes"
	"io"
	"log"
	"net/http"
	"strings"
	"github.com/gin-gonic/gin"
)

var backends = []string{"http://backend1:8080", "http://backend2:8080"}
var currentIndex int

func main() {
	r := gin.Default()
	r.GET("/proxy/*path", func(c *gin.Context) {
		path := c.Param("path")
		backend := backends[currentIndex]
		currentIndex = (currentIndex + 1) % len(backends)
		u, err := http.ParseURL(backend)
		if err != nil {
			log.Printf("Failed to parse backend URL: %v", err)
			c.AbortWithStatus(500)
			return
		}
		body, err := io.ReadAll(c.Request.Body)
		if err != nil {
			log.Printf("Failed to read request body: %v", err)
			c.AbortWithStatus(500)
			return
		}
		req, err := http.NewRequest(c.Request.Method, u.String()+path, bytes.NewBuffer(body))
		if err != nil {
			log.Printf("Failed to create request: %v", err)
			c.AbortWithStatus(500)
			return
		}
		for k, v := range c.Request.Header {
			req.Header.Set(k, strings.Join(v, ","))
		}
		req.Host = u.Host
		client := &http.Client{}
		resp, err := client.Do(req)
		if err != nil {
			log.Printf("Failed to forward request: %v", err)
			c.AbortWithStatus(500)
			return
		}
		for k, v := range resp.Header {
			c.Writer.Header().Set(k, strings.Join(v, ","))
		}
		c.Writer.WriteHeader(resp.StatusCode)
		_, err = io.Copy(c.Writer, resp.Body)
		if err != nil {
			log.Printf("Failed to copy response body: %v", err)
		}
		resp.Body.Close()
	})
	r.Run(":8080")
}
