package main

import (
	"github.com/gin-gonic/gin"
	"k8s.io/klog"
	"sync"
)

type Options struct {
	ListenAddr string
}

func Run(op *Options) error {
	var counter struct {
		value int
		mu    sync.Mutex
	}

	router := gin.Default()

	router.Use(func(c *gin.Context) {
		klog.Infof("Received %s %s", c.Request.Method, c.Request.URL.Path)
		c.Next()
	})

	api := router.Group("/api/v1")
	{
		api.GET("/ping", func(c *gin.Context) {
			c.String(200, "pong")
		})

		api.GET("/info", func(c *gin.Context) {
			counter.mu.Lock()
			defer counter.mu.Unlock()
			c.JSON(200, gin.H{
				"address": op.ListenAddr,
				"counter": counter.value,
			})
		})

		api.POST("/counter/increment", func(c *gin.Context) {
			counter.mu.Lock()
			counter.value++
			defer counter.mu.Unlock()
			c.JSON(200, gin.H{
				"counter": counter.value,
			})
		})

		api.POST("/counter/reset", func(c *gin.Context) {
			counter.mu.Lock()
			counter.value = 0
			defer counter.mu.Unlock()
			c.JSON(200, gin.H{
				"counter": counter.value,
			})
		})
	}

	return router.Run(op.ListenAddr)
}

