package main

import (
	"fmt"
	"math"
	"sync"
	"time"

	"github.com/gin-gonic/gin"
)

func RateLimit(rps int) gin.HandlerFunc {
	var (
		tokens     = rps
		lastRefill = time.Now()
		mu         sync.Mutex
	)
	return func(c *gin.Context) {
		mu.Lock()
		now := time.Now()
		elapsed := now.Sub(lastRefill)
		tokensToAdd := int(math.Floor(elapsed.Seconds() * float64(rps)))
		tokens += tokensToAdd
		if tokens > rps {
			tokens = rps
		}
		lastRefill = now
		if tokens <= 0 {
			mu.Unlock()
			c.AbortWithStatusJSON(429, gin.H{"error": "too many requests"})
			return
		}
		tokens--
		mu.Unlock()
		c.Next()
	}
}

func ginRun(rps int) {
	fmt.Printf("Rate limit set to %d requests per second\n", rps)
	r := gin.Default()
	r.Use(RateLimit(rps))
	r.GET("/rate", func(c *gin.Context) {
		c.JSON(200, gin.H{"message": "Request successful"})
	})
	r.Run(":8080")
}

