)

func generateRandomString(n int) string {
	var letters = []rune("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789")
	b := make([]rune, n)
	for i := range b {
		b[i] = letters[rand.Intn(len(letters))]
	}
	return string(b)
}

func main() {
	r := gin.Default()
	r.Static("/files", "/root/file/")

	r.POST("/upload", func(c *gin.Context) {
		file, err := c.FormFile("file")
		if err != nil {
			c.JSON(http.StatusBadRequest, gin.H{
				"status":  1,
				"message": "Error retrieving the file",
				"data":    nil,
			})
			return
		}

		timestamp := time.Now().UnixNano()
		randomStr := generateRandomString(6)
		ext := filepath.Ext(file.Filename)
		filename := fmt.Sprintf("%d-%s%s", timestamp, randomStr, ext)

		err = c.SaveUploadedFile(file, "/root/file/"+filename)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{
				"status":  1,
				"message": "Error saving the file",
				"data":    nil,
			})
			return
		}

		url := "http://localhost:8085/files/" + filename
		c.JSON(http.StatusOK, gin.H{
			"status":  0,
			"message": "File uploaded successfully",
			"data": gin.H{
				"url": url,
			},
		})
	})

	rand.Seed(time.Now().UnixNano())
	r.Run(":8085")
