		if err := c.Request.ParseMultipartForm(10 << 20); err != nil {
			c.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "Invalid request"})
			return
		}
		name := c.Request.FormValue("name")
		email := c.Request.FormValue("email")
		if name == "" || email == "" {
			c.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "Missing name or email"})
			return
		}
		file, handler, err := c.Request.FormFile("file")
		if err != nil {
			c.AbortWithStatusJSON(http.StatusBadRequest, gin.H{"error": "Error retrieving the file"})
			return
		}
		defer file.Close()
		dst := "./uploads/" + handler.Filename
		out, err := os.Create(dst)
		if err != nil {
			c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": "Error saving the file"})
			return
		}
		defer out.Close()
		if _, err := io.Copy(out, file); err != nil {
			c.AbortWithStatusJSON(http.StatusInternalServerError, gin.H{"error": "Error copying the file"})
			return
		}
		c.JSON(http.StatusOK, gin.H{
			"message":  "File uploaded successfully",
			"name":     name,
			"email":    email,
			"filename": handler.Filename,
		})
	})
	r.Run(":8080")
}
