func initConfig() *Config {
	return &Config{
		IP:     os.Getenv("IP"),
		Port:   os.Getenv("PORT"),
		Proxy:  os.Getenv("PROXY"),
		Token:  os.Getenv("TOKEN"),
	}
}

func main() {
	config := initConfig()

	// Setup HTTP client with proxy
	var httpClient *http.Client
	if config.Proxy != "" {
		proxyURL, _ := url.Parse(config.Proxy)
		httpClient = &http.Client{
			Transport: &http.Transport{
				Proxy: http.ProxyURL(proxyURL),
			},
		}
	} else {
		httpClient = &http.Client{}
	}

	r := gin.Default()

	// CORS middleware
	r.Use(func(c *gin.Context) {
		c.Header("Access-Control-Allow-Origin", "*")
		c.Header("Access-Control-Allow-Methods", "POST, GET, OPTIONS")
		c.Header("Access-Control-Allow-Headers", "Content-Type, Authorization")
		if c.Request.Method == "OPTIONS" {
			c.AbortWithStatus(200)
			return
		}
		c.Next()
	})

	// Authentication middleware
	authMiddleware := func() gin.HandlerFunc {
		return func(c *gin.Context) {
			token := c.GetHeader("Authorization")
			if token != "Bearer "+config.Token {
				c.AbortWithStatusJSON(401, gin.H{"error": "unauthorized"})
				return
			}
			c.Next()
		}
	}

	// Root endpoint
	r.GET("/", func(c *gin.Context) {
		c.JSON(200, gin.H{
			"message": "Translation Server",
			"ip":      config.IP,
			"port":    config.Port,
			"proxy":   config.Proxy,
		})
	})

	// Translate endpoint
	r.POST("/translate", authMiddleware(), func(c *gin.Context) {
		var req struct {
			Text       string `json:"text"`
			SourceLang string `json:"source_lang"`
			TargetLang string `json:"target_lang"`
		}
		if err := c.BindJSON(&req); err != nil {
			c.AbortWithStatusJSON(400, gin.H{"error": "invalid request"})
			return
		}

		payload := map[string]interface{}{
			"text":        req.Text,
			"source_lang": req.SourceLang,
			"target_lang": req.TargetLang,
		}
		jsonData, _ := json.Marshal(payload)

		resp, err := httpClient.Post("https://api.deeplx.example/translate", "application/json", bytes.NewBuffer(jsonData))
		if err != nil {
			c.AbortWithStatusJSON(500, gin.H{"error": "translation failed"})
			return
		}
		defer resp.Body.Close()

		body, _ := io.ReadAll(resp.Body)
		var result map[string]interface{}
		json.Unmarshal(body, &result)

		c.JSON(200, result)
	})

