func NewClient(ctx context.Context, config *ClientConfig) (*Client, error) {
	serverPicker, err := protocol.NewRoundRobinServerPicker(config.Servers)
	if err != nil {
		return nil, err
	}
	policyManager := policy.NewManager(config.Policies)
	return &Client{
		serverPicker:   serverPicker,
		policyManager:  policyManager,
	}, nil
}

func (c *Client) Process(ctx context.Context, link *transport.Link, dialer internet.Dialer) error {
	server := c.serverPicker.PickServer()
	if server == nil {
		return errors.New("no server available")
	}

	conn, err := dialer.DialContext(ctx, "tcp", server.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	if err := c.policyManager.ApplyTimeouts(ctx, conn); err != nil {
		return err
	}

	if err := c.handshake(ctx, conn, link); err != nil {
		return err
	}

	if err := c.transferData(ctx, conn, link); err != nil {
		return err
	}

	return nil
}

func (c *Client) handshake(ctx context.Context, conn net.Conn, link *transport.Link) error {
	// Example handshake: send client identifier
	if _, err := conn.Write([]byte("CLIENT")); err != nil {
		return err
	}
	return nil
}

func (c *Client) transferData(ctx context.Context, conn net.Conn, link *transport.Link) error {
	buf := make([]byte, 1024)
	for {
		select {
		case <-ctx.Done():
			return ctx.Err()
		default:
			n, err := conn.Read(buf)
			if err != nil {
				return err
			}
			if _, err := link.Write(buf[:n]); err != nil {
				return err
			}
		}
	}
}
