package main

import (
	"context"
	"crypto/rand"
	"errors"
	"net"
)

type DNSEndpoint struct {
	Addr string
}

func (ep *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (n int, err error) {
	addr, err := net.ResolveUDPAddr("udp", ep.Addr)
	if err != nil {
		return 0, err
	}

	conn, err := net.DialUDP("udp", nil, addr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if deadline, ok := ctx.Deadline(); ok {
		conn.SetDeadline(deadline)
	}

	id := make([]byte, 2)
	if _, err := rand.Read(id); err != nil {
		return 0, err
	}

	payload[0] = id[0]
	payload[1] = id[1]

	if _, err := conn.Write(payload); err != nil {
		return 0, err
	}

	n, err = conn.Read(buf)
	if err != nil {
		return n, err
	}

	if buf[0] != id[0] || buf[1] != id[1] {
		return n, errors.New("transaction ID mismatch")
	}

	return n, nil
}
