	Registry      Registry
	FlushInterval time.Duration
	DurationUnit  time.Duration
	Prefix        string
}

func OpenTSDBWithConfig(c OpenTSDBConfig) {
	ticker := time.NewTicker(c.FlushInterval)
	go func() {
		for {
			select {
			case <-ticker.C:
				if err := openTSDB(&c); err != nil {
					// Handle error as needed
				}
			}
		}
	}()
}

func openTSDB(c *OpenTSDBConfig) error {
	conn, err := net.DialTCP("tcp", nil, c.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	metrics := c.Registry.All()
	for name, metric := range metrics {
		var value float64
		switch m := metric.(type) {
		case *Counter:
			value = float64(m.Count())
		case *Gauge:
			value = m.Value()
		case *Histogram:
			value = float64(m.Count())
		case *Meter:
			value = m.MeanRate()
		case *Timer:
			meanDur := m.Mean()
			value = float64(meanDur.Nanoseconds()) / float64(c.DurationUnit.Nanoseconds())
		default:
			continue
		}
		metricName := c.Prefix + name
		timestamp := time.Now().UnixNano() / 1e6
		line := fmt.Sprintf("%s %d %f\n", metricName, timestamp, value)
		if _, err := conn.Write([]byte(line)); err != nil {
			return err
		}
	}
	return nil
}

