	var err error

	if c.DialTimeout > 0 {
		c.Dialer.Timeout = c.DialTimeout
	}

	conn, err = c.Dialer.Dial(c.Net, address)
	if err != nil {
		return nil, err
	}

	if c.TLSConfig != nil {
		tlsConn := tls.Client(conn, c.TLSConfig)
		if c.ReadTimeout > 0 {
			tlsConn.SetReadDeadline(time.Now().Add(c.ReadTimeout))
		}
		if c.WriteTimeout > 0 {
			tlsConn.SetWriteDeadline(time.Now().Add(c.WriteTimeout))
		}
		conn = tlsConn
	} else {
		if c.ReadTimeout > 0 {
			conn.SetReadDeadline(time.Now().Add(c.ReadTimeout))
		}
		if c.WriteTimeout > 0 {
			conn.SetWriteDeadline(time.Now().Add(c.WriteTimeout))
		}
	}

	return &Conn{Conn: conn}, nil
}

func (c *Client) Exchange(m *Msg, address string) (*Msg, time.Duration, error) {
	ctx, cancel := context.WithTimeout(context.Background(), c.Timeout)
	defer cancel()

	var resp *Msg
	var err error

	if c.SingleInflight {
		resp, err, _ = c.group.Do(address, func() (interface{}, error) {
			return c.exchange(m, address, ctx)
		})
	} else {
		resp, err = c.exchange(m, address, ctx)
	}

	if err != nil {
		return nil, 0, err
	}

	return resp, 0, nil
}

func (c *Client) exchange(m *Msg, address string, ctx context.Context) (*Msg, error) {
	conn, err := c.Dial(address)
	if err != nil {
		return nil, err
	}
	defer conn.Close()

	data, err := m.Pack()
	if err != nil {
		return nil, err
	}

	length := uint16(len(data))
	buf := make([]byte, 2+len(data))
	binary.BigEndian.PutUint16(buf[:2], length)
	copy(buf[2:], data)

	if err := conn.SetWriteDeadline(time.Now().Add(c.WriteTimeout)); err != nil {
		return nil, err
	}

	if _, err := conn.Write(buf); err != nil {
		return nil, err
	}

	if err := conn.SetReadDeadline(time.Now().Add(c.ReadTimeout)); err != nil {
		return nil, err
	}

	lengthBuf := make([]byte, 2)
	if _, err := io.ReadFull(conn, lengthBuf); err != nil {
		return nil, err
	}

	length = binary.BigEndian.Uint16(lengthBuf)
	responseData := make([]byte, length)

	if _, err := io.ReadFull(conn, responseData); err != nil {
		return nil, err
	}

	resp, err := Unpack(responseData)
	if err != nil {
		return nil, err
	}

	return resp, nil
}

func (c *Client) getTimeoutForRequest(timeout time.Duration) time.Duration {
	if timeout == 0 {
		return c.Timeout
	}
	return timeout
