package main

import (
	"context"
	"io"
	"net"
	"os"
	"strconv"
	"time"
)

type Config struct {
	Path         string
	Mode         string
	Timeout      time.Duration
	MaxConnections int
	MaxBytes     int
}

type unix struct {
	name      string
	config    *Config
	done      chan struct{}
	eventPool *event.Pool
}

func (u *unix) ProductLoop(productFunc api.ProductFunc) {
	if err := u.checkBind(u.config.Path); err != nil {
		return
	}
	if err := u.chmod(u.config.Path, u.config.Mode); err != nil {
		return
	}
	ln, err := net.Listen("unix", u.config.Path)
	if err != nil {
		return
	}
	defer ln.Close()
	connChan := make(chan struct{}, u.config.MaxConnections)
	for {
		select {
		case <-u.done:
			return
		default:
			conn, err := ln.Accept()
			if err != nil {
				continue
			}
			select {
			case connChan <- struct{}{}:
				go u.handleConn(context.Background(), conn, productFunc)
			default:
				conn.Close()
			}
		}
	}
}

func (u *unix) handleConn(ctx context.Context, conn net.Conn, productFunc api.ProductFunc) {
	defer func() {
		<-u.eventPool.Get()
		conn.Close()
	}()
	deadline := time.Now().Add(u.config.Timeout)
	if err := conn.SetDeadline(deadline); err != nil {
		return
	}
	buf := make([]byte, u.config.MaxBytes)
	n, err := conn.Read(buf)
	if err != nil && err != io.EOF {
		return
	}
	productFunc(buf[:n])
}

func (u *unix) checkBind(path string) error {
	if _, err := os.Stat(path); err == nil {
		if err := os.Remove(path); err != nil {
			return err
		}
	}
	return nil
}

func (u *unix) chmod(path string, mode string) error {
	m, err := strconv.ParseInt(mode, 8, 32)
	if err != nil {
		return err
	}
	return os.Chmod(path, os.FileMode(m))
}
