	if !ok {
		return
	}
	file, err := tcpConn.File()
	if err != nil {
		return
	}
	fd := file.Fd()

	trafficClass := 0x01
	hopLimit := 64
	syscall.SetsockoptInt(int(fd), syscall.IPPROTO_IPV6, syscall.IPV6_TCLASS, trafficClass)
