func printFeature(client routeguide.RouteGuideClient, point *routeguide.Point) {
	log.Printf("Getting feature for point (%d, %d)", point.Latitude, point.Longitude)
	feature, err := client.GetFeature(point)
	if err != nil {
		log.Fatalf("Failed to get feature: %v", err)
	}
	log.Printf("Feature: %v", feature)
}

func printFeatures(client routeguide.RouteGuideClient, rectangle *routeguide.Rectangle) {
	log.Println("Listing features within rectangle")
	stream, err := client.ListFeatures(rectangle)
	if err != nil {
		log.Fatalf("Failed to list features: %v", err)
	}
	for {
		feature, err := stream.Recv()
		if err != nil {
			break
		}
		log.Printf("Feature: %v", feature)
	}
}

func runRecordRoute(client routeguide.RouteGuideClient) {
	log.Println("Recording route")
	points := []*routeguide.Point{
		{Latitude: 409146138, Longitude: -746188934},
		{Latitude: 409146138, Longitude: -746188934},
		{Latitude: 409146138, Longitude: -746188934},
	}
	stream, err := client.RecordRoute()
	if err != nil {
		log.Fatalf("Failed to start record route: %v", err)
	}
	for _, point := range points {
		if err := stream.Send(point); err != nil {
			log.Fatalf("Failed to send point: %v", err)
		}
	}
	reply, err := stream.CloseAndRecv()
	if err != nil {
		log.Fatalf("Failed to receive reply: %v", err)
	}
	log.Printf("Route summary: %v", reply)
}

func runRouteChat(client routeguide.RouteGuideClient) {
	log.Println("Starting route chat")
	stream, err := client.RouteChat()
	if err != nil {
		log.Fatalf("Failed to start route chat: %v", err)
	}
	go func() {
		for {
			notes := []*routeguide.RouteNote{
				{Location: &routeguide.Point{Latitude: 409146138, Longitude: -746188934}, Message: "First message"},
				{Location: &routeguide.Point{Latitude: 409146138, Longitude: -746188934}, Message: "Second message"},
			}
			for _, note := range notes {
				if err := stream.Send(note); err != nil {
					log.Fatalf("Failed to send note: %v", err)
				}
				time.Sleep(1 * time.Second)
			}
		}
	}()
	for {
		reply, err := stream.Recv()
		if err != nil {
			break
		}
		log.Printf("Received: %v", reply)
	}
}

func main() {
	tls := flag.Bool("tls", false, "Connection uses TLS if true")
	serverHostOverride := flag.String("server_host_override", "", "Server name for TLS")
	caFile := flag.String("ca_file", "", "Path to CA certificate file")
	serverAddr := flag.String("server_addr", "localhost:50051", "Server address in format host:port")

	flag.Parse()

	var opts []grpc.DialOption
	if *tls {
		creds, err := credentials.NewClientTLSFromFile(*caFile, *serverHostOverride)
		if err != nil {
			log.Fatalf("Failed to create TLS credentials: %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(*serverAddr, opts...)
	if err != nil {
		log.Fatalf("Failed to connect: %v", err)
	}
	defer conn.Close()

	client := routeguide.NewRouteGuideClient(conn)

	printFeature(client, &routeguide.Point{Latitude: 409146138, Longitude: -746188934})
	printFeatures(client, &routeguide.Rectangle{
		Lo: &routeguide.Point{Latitude: 400000000, Longitude: -750000000},
		Hi: &routeguide.Point{Latitude: 420000000, Longitude: -730000000},
	})
	runRecordRoute(client)
	runRouteChat(client)
}
