	"io"
)

func doClientWork() {
	conn, err := grpc.Dial("localhost:1234", grpc.WithInsecure())
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()

	client := NewYourServiceClient(conn)

	// Unary call
	resp, err := client.Hello(context.Background(), &HelloRequest{})
	if err != nil {
		log.Fatalf("Hello call failed: %v", err)
	}
	fmt.Println(resp.Message)

	// Bidirectional streaming
	stream, err := client.Channel(context.Background())
	if err != nil {
		log.Fatalf("Channel call failed: %v", err)
	}

	// Send messages in a goroutine
	go func() {
		ticker := time.NewTicker(1 * time.Second)
		for {
			select {
			case <-ticker.C:
				err := stream.Send(&Message{Text: "Hello from client"})
				if err != nil {
					log.Printf("Send failed: %v", err)
				}
			}
		}
	}()

	// Receive messages in main loop
	for {
		msg, err := stream.Recv()
		if err == io.EOF {
			break
		}
		if err != nil {
			if st, ok := status.FromError(err); ok {
				log.Printf("Recv failed: %v", st.Message())
			} else {
				log.Printf("Recv failed: %v", err)
			}
			break
		}
		fmt.Println("Received:", msg.Text)
	}
}

