		if err == io.EOF {
			break
		} else if err != nil {
			return err
		}
		metrics = append(metrics, metric)
	}
	_, err := s.SendMetrics(context.Background(), &forwardrpc.MetricList{
		Metrics: metrics,
	})
	return err
}

