	"github.com/hyperledger/fabric/tlsgen"
)

type ccSrv struct {
	l             net.Listener
	grpcSrv       *grpc.Server
	t             *testing.T
	expectedCCname string
}

func (s *ccSrv) Register(stream peer.ChaincodeSupport_RegisterServer) error {
	for {
		msg, err := stream.Recv()
		if err == io.EOF {
			return nil
		}
		if err != nil {
			return err
		}
		if msg.ChaincodeName != s.expectedCCname {
			return status.Errorf(codes.InvalidArgument, "unexpected chaincode name: got %s, expected %s", msg.ChaincodeName, s.expectedCCname)
		}
		if err := stream.Send(msg); err != nil {
			return err
		}
	}
}

func (s *ccSrv) stop() {
	s.grpcSrv.Stop()
	s.l.Close()
}

func newCCServer(t *testing.T, port int, expectedCCname string, withTLS bool, ca tlsgen.CA) *ccSrv {
	addr := fmt.Sprintf("localhost:%d", port)
	l, err := net.Listen("tcp", addr)
	if err != nil {
		t.Fatalf("failed to listen: %v", err)
	}

	var opts []grpc.ServerOption
	if withTLS {
		cert, err := ca.GenerateCert(addr, nil, nil, tlsgen.ServerUsage)
		if err != nil {
			t.Fatalf("failed to generate cert: %v", err)
		}
		tlsConfig := &tls.Config{
			Certificates: []tls.Certificate{{Leaf: cert.Cert, Key: cert.Key}},
			ClientCAs:    ca.CertPool(),
			ClientAuth:   tls.RequireAndVerifyClientCert,
		}
		opts = append(opts, grpc.Creds(credentials.NewTLS(tlsConfig)))
	}

	grpcSrv := grpc.NewServer(opts...)
	peer.RegisterChaincodeSupportServer(grpcSrv, &ccSrv{t: t, expectedCCname: expectedCCname})

	return &ccSrv{
		l:             l,
		grpcSrv:       grpcSrv,
		t:             t,
		expectedCCname: expectedCCname,
	}
}
