		grpcServer: grpc.NewServer(),
		bufferSize: 1024,
	}
}

func (s *Server) Start(url *common.URL) {
	addr := url.String()
	lis, err := net.Listen("tcp", addr)
	if err != nil {
		log.Fatalf("failed to listen: %v", err)
	}

	var opts []grpc.ServerOption

	// Configure message size
	opts = append(opts, grpc.MaxRecvMsgSize(s.bufferSize))
	opts = append(opts, grpc.MaxSendMsgSize(s.bufferSize))

	// TLS setup
	if url.TLSConfig != nil {
		tlsConfig := &tls.Config{
			Certificates: []tls.Certificate{url.TLSConfig.Cert},
		}
		opts = append(opts, grpc.Creds(credentials.NewTLS(tlsConfig)))
	}

	// OpenTracing integration
	tracer, _ := opentracing.GlobalTracer()
	opts = append(opts, grpc.UnaryInterceptor(opentracing.UnaryServerInterceptor(tracer)))

	s.grpcServer = grpc.NewServer(opts...)
	// Register services here
	reflection.Register(s.grpcServer)

	if err := s.grpcServer.Serve(lis); err != nil {
		log.Fatalf("failed to serve: %v", err)
	}
}

