package main

import (
	"context"
	"fmt"
	"net"
	"sync"

	"github.com/spf13/viper"
	"google.golang.org/grpc"
	"your/protobuf/package"
)

type server struct {
	protobuf.UnimplementedAccServerServer
	users     map[string]bool
	userConns map[string]chan *protobuf.Message
	mu        sync.Mutex
}

func (s *server) QueryUsersOnline(ctx context.Context, req *protobuf.QueryUsersOnlineReq) (*protobuf.QueryUsersOnlineRsp, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	online := s.users[req.UserId]
	return &protobuf.QueryUsersOnlineRsp{Online: online}, nil
}

func (s *server) SendMsg(ctx context.Context, req *protobuf.SendMsgReq) (*protobuf.SendMsgRsp, error) {
	s.mu.Lock()
	conn, ok := s.userConns[req.UserId]
	s.mu.Unlock()
	if ok {
		conn <- &protobuf.Message{Content: req.Content}
	}
	return &protobuf.SendMsgRsp{}, nil
}

func (s *server) SendMsgAll(ctx context.Context, req *protobuf.SendMsgAllReq) (*protobuf.SendMsgAllRsp, error) {
	s.mu.Lock()
	for _, conn := range s.userConns {
		conn <- &protobuf.Message{Content: req.Content}
	}
	s.mu.Unlock()
	return &protobuf.SendMsgAllRsp{}, nil
}

func (s *server) GetUserList(ctx context.Context, req *protobuf.GetUserListReq) (*protobuf.GetUserListRsp, error) {
	s.mu.Lock()
	var users []*protobuf.User
	for user := range s.users {
		users = append(users, &protobuf.User{Id: user})
	}
	s.mu.Unlock()
	return &protobuf.GetUserListRsp{Users: users}, nil
}

func (s *server) Init() {
	viper.SetDefault("grpc.port", 50051)
	port := viper.GetInt("grpc.port")
	lis, _ := net.Listen("tcp", fmt.Sprintf(":%d", port))
	grpcServer := grpc.NewServer()
	protobuf.RegisterAccServerServer(grpcServer, s)
	grpcServer.Serve(lis)
}
