	dial               func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	netDial            func(context.Context, string) (net.Conn, error)
	creds              credentials.TransportCredentials
	certWatcher        *certwatcher.CertWatcher
}

func (c *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	listener, dial, netDial, err := c.listenerAndDialer()
	if err != nil {
		return nil, err
	}

	tlsOpts, certWatcher, err := c.tlsOpts()
	if err != nil {
		return nil, err
	}

	serverOpts := append(tlsOpts, opts...)
	if c.MaxConnAge > 0 {
		serverOpts = append(serverOpts, grpc.MaxConcurrentStreams(c.MaxWorkers))
	}

	return &completedGRPCServer{
		opts:              serverOpts,
		listener:          listener,
		svcRegistrationFn: svcRegistrationFn,
		listenFunc: func() error {
			srv := grpc.NewServer(serverOpts...)
			svcRegistrationFn(srv)
			return srv.Serve(listener)
		},
		stopFunc: func() {
			// Implement graceful stop logic
		},
		dial:      dial,
		netDial:   netDial,
		creds:     c.clientCreds(),
		certWatcher: certWatcher,
	}, nil
}

func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error), func(context.Context, string) (net.Conn, error), error) {
	var listener net.Listener
	var dialFunc func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	var netDialFunc func(context.Context, string) (net.Conn, error)

	switch c.Network {
	case "tcp":
		l, err := net.Listen("tcp", c.Address)
		if err != nil {
			return nil, nil, nil, err
		}
		listener = l
		dialFunc = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, c.Address, opts...)
		}
		netDialFunc = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.Dial("tcp", addr)
		}
	case "unix":
		l, err := net.Listen("unix", c.Address)
		if err != nil {
			return nil, nil, nil, err
		}
		listener = l
		dialFunc = func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
			return grpc.DialContext(ctx, "unix:"+c.Address, opts...)
		}
		netDialFunc = func(ctx context.Context, addr string) (net.Conn, error) {
			return net.Dial("unix", addr)
		}
	case "buffered":
		// Placeholder for buffered network implementation
		// Requires custom listener and dialer
		return nil, nil, nil, nil
	default:
		return nil, nil, nil, nil
	}

	return listener, dialFunc, netDialFunc, nil
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	var tlsOpts []grpc.ServerOption
	var certWatcher *certwatcher.CertWatcher

	// Load TLS certificate and key
	tlsConfig, err := loadTLSConfig(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	// Set up certificate watcher
	if c.TLSCertPath != "" && c.TLSKeyPath != "" {
		certWatcher, err = certwatcher.New(c.TLSCertPath, c.TLSKeyPath)
		if err != nil {
			return nil, nil, err
		}
		tlsOpts = append(tlsOpts, grpc.Creds(credentials.NewTLS(tlsConfig)))
	}

	// Configure client authentication if CA path is provided
	if c.ClientCAPath != "" {
		caCert, err := loadCACert(c.ClientCAPath)
		if err != nil {
			return nil, nil, err
		}
		tlsConfig.ClientCAs = caCert
		tlsConfig.ClientAuth = tls.RequireAndVerifyClientCert
	}

	return tlsOpts, certWatcher, nil
}

func (c *GRPCServerConfig) clientCreds() credentials.TransportCredentials {
	if c.ClientCAPath != "" {
		caCert, _ := loadCACert(c.ClientCAPath)
		return credentials.NewTLS(&tls.Config{RootCAs: caCert})
	}
	return credentials.InsecureCredentials()
}

func (s *completedGRPCServer) WithOpts(opts ...grpc.ServerOption) RunnableGRPCServer {
	s.opts = append(s.opts, opts...)
	return s
}

func (s *completedGRPCServer) Listen(ctx context.Context) func() error {
