}

func (s *completedGRPCServer) NetDialContext(ctx context.Context, addr string) (net.Conn, error) {
	return s.netDial(ctx, addr)
}

func (s *completedGRPCServer) Insecure() bool {
	return s.creds == credentials.InsecureCredentials()
}

