package main

import (
	"errors"
	"time"

	"github.com/streadway/amqp"
)

type Consumer struct {
	conn    *amqp.Connection
	channel *amqp.Channel
	tag     string
	done    chan error
}

func NewConsumer(amqpURI, exchange, exchangeType, queueName, key, ctag string) (*Consumer, error) {
	conn, err := amqp.Dial(amqpURI)
	if err != nil {
		return nil, err
	}
	channel, err := conn.Channel()
	if err != nil {
		return nil, err
	}
	if err := channel.ExchangeDeclare(
		exchange,
		exchangeType,
		false,
		false,
		false,
		false,
		nil,
	); err != nil {
		return nil, err
	}
	if _, err := channel.QueueDeclare(
		queueName,
		false,
		false,
		false,
		false,
		nil,
	); err != nil {
		return nil, err
	}
	if err := channel.QueueBind(queueName, key, exchange, false, nil); err != nil {
		return nil, err
	}
	deliveries, err := channel.Consume(
		queueName,
		ctag,
		false,
		false,
		false,
		false,
		nil,
	)
	if err != nil {
		return nil, err
	}
	c := &Consumer{
		conn:    conn,
		channel: channel,
		tag:     ctag,
		done:    make(chan error),
	}
	go c.handle(deliveries, c.done)
	return c, nil
}

func (c *Consumer) Shutdown() error {
	if err := c.channel.Cancel(c.tag, true); err != nil {
		return err
	}
	if err := c.channel.Close(); err != nil {
		return err
	}
	if err := c.conn.Close(); err != nil {
		return err
	}
	select {
	case err := <-c.done:
		return err
	case <-time.After(5 * time.Second):
		return errors.New("timeout waiting for shutdown")
	}
}

func (c *Consumer) handle(deliveries <-chan amqp.Delivery, done chan error) {
	for d := range deliveries {
		d.Ack(false)
	}
	done <- nil
}

