package main

import (
	"bytes"
	"context"
	"io"
	"net/url"
	"github.com/gorilla/websocket"
)

type Listener struct {
	URL        *url.URL
	Token      string
	SchemaFunc func([]byte) (io.Reader, error)
	Out        io.Writer
	InCh       <-chan []byte
}

func NewListener(url *url.URL, token string, schemaFunc func([]byte) (io.Reader, error), out io.Writer, inCh <-chan []byte) *Listener {
	return &Listener{
		URL:        url,
		Token:      token,
		SchemaFunc: schemaFunc,
		Out:        out,
		InCh:       inCh,
	}
}

func (l *Listener) Listen(ctx context.Context) error {
	u := *l.URL
	q := u.Query()
	q.Set("token", l.Token)
	u.RawQuery = q.Encode()
	urlStr := u.String()

	wsConn, _, err := websocket.DefaultDialer.Dial(urlStr, nil)
	if err != nil {
		return err
	}
	defer wsConn.Close()

	errChan := make(chan error, 1)

	go func() {
		for {
			select {
			case <-ctx.Done():
				errChan <- ctx.Err()
				return
			default:
				messageType, message, err := wsConn.ReadMessage()
				if err != nil {
					errChan <- err
					return
				}
				if messageType == websocket.CloseMessage {
					errChan <- nil
					return
				}
				var reader io.Reader
				if l.SchemaFunc != nil {
					reader, err = l.SchemaFunc(message)
					if err != nil {
						errChan <- err
						return
					}
				} else {
					reader = bytes.NewReader(message)
				}
				if _, err := io.Copy(l.Out, reader); err != nil {
					errChan <- err
					return
				}
			}
		}
	}()

	go func() {
		for {
			select {
			case <-ctx.Done():
				errChan <- ctx.Err()
				return
			case msg, ok := <-l.InCh:
				if !ok {
					errChan <- nil
					return
				}
				if err := wsConn.WriteMessage(websocket.BinaryMessage, msg); err != nil {
					errChan <- err
					return
				}
			}
		}
	}()

	return <-errChan
}
