package main

import (
	"errors"
	"fmt"
	"net"
	"strconv"
	"strings"
)

func WakeUp(macAddr string, broadcastIP string, bcastInterface string, port int) error {
	macBytes, err := parseMAC(macAddr)
	if err != nil {
		return err
	}

	magicPacket := make([]byte, 102)
	for i := 0; i < 6; i++ {
		magicPacket[i] = 0xFF
	}
	for i := 0; i < 16; i++ {
		copy(magicPacket[6+i*6:], macBytes)
	}

	ip := net.ParseIP(broadcastIP)
	if ip == nil {
		return errors.New("invalid broadcast IP")
	}

	var conn *net.UDPConn
	var err error
	if bcastInterface != "" {
		ifaceIP, err := getInterfaceIP(bcastInterface)
		if err != nil {
			return err
		}
		localAddr := &net.UDPAddr{IP: ifaceIP}
		conn, err = net.DialUDP("udp", localAddr, &net.UDPAddr{IP: ip, Port: port})
	} else {
		conn, err = net.DialUDP("udp", nil, &net.UDPAddr{IP: ip, Port: port})
	}
	if err != nil {
		return err
	}
	defer conn.Close()

	if err := conn.SetBroadcast(true); err != nil {
		return err
	}

	_, err = conn.Write(magicPacket)
	return err
}

func parseMAC(s string) ([]byte, error) {
	parts := strings.Split(s, ":")
	if len(parts) != 6 {
		return nil, fmt.Errorf("invalid MAC address format")
	}
	result := make([]byte, 6)
	for i, part := range parts {
		val, err := strconv.ParseUint(part, 16, 8)
		if err != nil {
			return nil, err
		}
		result[i] = byte(val)
	}
	return result, nil
}

func getInterfaceIP(ifaceName string) (net.IP, error) {
	ifaces, err := net.Interfaces()
	if err != nil {
		return nil, err
	}
	for _, iface := range ifaces {
		if iface.Name == ifaceName {
			addrs, err := iface.Addrs()
			if err != nil {
				return nil, err
			}
			for _, addr := range addrs {
				if ip, ok := addr.(*net.IPNet); ok && !ip.IP.IsLoopback() && ip.IP.To4() != nil {
					return ip.IP, nil
				}
			}
		}
	}
	return nil, fmt.Errorf("no non-loopback IPv4 address found on interface %s", ifaceName)
}
