package main

import (
	"context"
	"net"
	"sync"
	"time"
	"unsafe"

	"github.com/miekg/dns"
	"go.uber.org/zap"
	"google.golang.org/grpc/netip"
)

type Bootstrap struct {
	fqdn       string
	port       uint16
	bootstrap  *net.UDPAddr
	qt         uint16
	logger     *zap.Logger
	updating   atomic.Bool
	nextUpdate time.Time
	readyNotify chan struct{}
	m          sync.Mutex
	ready      bool
	addrStr    string
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	b.m.Lock()
	if b.ready {
		b.m.Unlock()
		addr, err := netip.ParseAddr(b.addrStr)
		if err != nil {
			return netip.Addr{}, 0, err
		}
		return addr, 0, nil
	}
	if b.updating.Load() {
		b.m.Unlock()
		select {
		case <-b.readyNotify:
			b.m.Lock()
			if b.ready {
				b.m.Unlock()
				addr, err := netip.ParseAddr(b.addrStr)
				if err != nil {
					return netip.Addr{}, 0, err
				}
				return addr, 0, nil
			}
			b.m.Unlock()
		case <-ctx.Done():
			return netip.Addr{}, 0, ctx.Err()
		}
	}
	b.updating.Store(true)
	b.m.Unlock()

	var addrStr string
	var ttl uint32
	var err error

	for i := 0; i < 3; i++ {
		conn, err := net.DialUDP("udp", nil, b.bootstrap)
		if err != nil {
			b.logger.Error("Failed to dial UDP", zap.Error(err))
			if i == 2 {
				return netip.Addr{}, 0, err
			}
			time.Sleep(1 * time.Second)
			continue
		}
		defer conn.Close()

		msg := new(dns.Msg)
		msg.Id = dns.Id()
		msg.RecursionDesired = true
		msg.Question = []dns.Question{
			{
				Name:   b.fqdn,
				Qtype:  qt,
				Qclass: dns.ClassINET,
			},
		}

		buf, err := msg.Pack()
		if err != nil {
			b.logger.Error("Failed to pack DNS message", zap.Error(err))
			if i == 2 {
				return netip.Addr{}, 0, err
			}
			time.Sleep(1 * time.Second)
			continue
		}

		_, err = conn.Write(buf)
		if err != nil {
			b.logger.Error("Failed to send DNS query", zap.Error(err))
			if i == 2 {
				return netip.Addr{}, 0, err
			}
			time.Sleep(1 * time.Second)
			continue
		}

		conn.SetReadDeadline(time.Now().Add(5 * time.Second))
		response := make([]byte, 512)
		n, err := conn.Read(response)
		if err != nil {
			b.logger.Error("Failed to read DNS response", zap.Error(err))
			if i == 2 {
				return netip.Addr{}, 0, err
			}
			time.Sleep(1 * time.Second)
			continue
		}

		respMsg := new(dns.Msg)
		err = respMsg.Unpack(response[:n])
		if err != nil {
			b.logger.Error("Failed to unpack DNS response", zap.Error(err))
			if i == 2 {
				return netip.Addr{}, 0, err
			}
			time.Sleep(1 * time.Second)
			continue
		}

		if respMsg.Rcode != dns.RcodeSuccess {
			b.logger.Error("DNS query failed", zap.Uint16("rcode", respMsg.Rcode))
			if i == 2 {
				return netip.Addr{}, 0, fmt.Errorf("DNS query failed: %v", respMsg.Rcode)
			}
			time.Sleep(1 * time.Second)
			continue
		}

		for _, ans := range respMsg.Answer {
			if ans.Header().Rrtype == qt {
				if a, ok := ans.(*dns.A); ok {
					addrStr = a.A.String()
					ttl = a.Header().Ttl
					break
				}
				if aaaa, ok := ans.(*dns.AAAA); ok {
					addrStr = aaaa.AAAA.String()
					ttl = aaaa.Header().Ttl
					break
				}
			}
		}

		if addrStr != "" {
			break
		}

		if i == 2 {
			return netip.Addr{}, 0, fmt.Errorf("no answer found")
		}
		time.Sleep(1 * time.Second)
	}

	b.m.Lock()
	b.addrStr = addrStr
	b.ready = true
	b.updating.Store(false)
	close(b.readyNotify)
	b.m.Unlock()

	addr, err := netip.ParseAddr(addrStr)
	if err != nil {
		return netip.Addr{}, 0, err
	}
	return addr, ttl, nil
}
