package main

import (
	"fmt"
	"net"
	"time"
)

type network struct {
	gateway net.IP
}

func (n *network) call(msg []byte, timeout time.Duration) ([]byte, error) {
	maxRetries := 3
	backoff := time.Second
	for attempt := 0; attempt < maxRetries; attempt++ {
		addr := &net.UDPAddr{
			IP:   n.gateway,
			Port: 5351,
		}
		conn, err := net.DialUDP("udp", nil, addr)
		if err != nil {
			if attempt < maxRetries-1 {
				time.Sleep(backoff)
				backoff *= 2
			}
			continue
		}
		defer conn.Close()

		deadline := time.Now().Add(timeout)
		conn.SetDeadline(deadline)

		if _, err := conn.Write(msg); err != nil {
			if attempt < maxRetries-1 {
				time.Sleep(backoff)
				backoff *= 2
			}
			continue
		}

		buf := make([]byte, 1024)
		nRead, err := conn.Read(buf)
		if err != nil {
			if attempt < maxRetries-1 {
				time.Sleep(backoff)
				backoff *= 2
			}
			continue
		}

		return buf[:nRead], nil
	}
	return nil, fmt.Errorf("all retries failed")
}
