// extern int spawn_userns_cat(const char *nsPath, const char *path, int stdout_fd, int stderr_fd);
import "C"

func spawnUserNamespaceCat(nsPath, path string) ([]byte, error) {
	stdoutR, stdoutW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stdoutR.Close()
	defer stdoutW.Close()

	stderrR, stderrW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stderrR.Close()
	defer stderrW.Close()

	cNsPath := C.CString(nsPath)
	cPath := C.CString(path)
	defer C.free(unsafe.Pointer(cNsPath))
	defer C.free(unsafe.Pointer(cPath))

	ret := C.spawn_userns_cat(cNsPath, cPath, C.int(stdoutW.Fd()), C.int(stderrW.Fd()))
	if ret != 0 {
		stderrData, _ := io.ReadAll(stderrR)
		return nil, &os.SyscallError{Syscall: "spawn_userns_cat", Err: os.Errno(ret)}
	}

	stdoutData, err := io.ReadAll(stdoutR)
	if err != nil {
		return nil, err
	}

	return stdoutData, nil
}

