		if err != nil {
			return err
		}
	case <-ctx.Done():
		if err := cmd.Process.Kill(); err != nil {
			return err
		}
		return ctx.Err()
	}

	pwdBytes := []byte(password)
	for i := range pwdBytes {
		pwdBytes[i] = 0
	}

	return nil
}
