func (a *API) New(cfg *bridge.Config) bridge.Bridger {
	a.Messages = ring.New(cfg.MessageCapacity)
	a.Config = cfg
	a.mrouter = melody.New()
	a.mrouter.OnMessage = func(s *melody.Session, msg []byte) {
		var message config.Message
		if err := json.Unmarshal(msg, &message); err != nil {
			log.Println("Error unmarshalling message:", err)
			return
		}
		a.handleWebsocketMessage(message, s)
	}
	e := echo.New()
	e.GET("/health", a.handleHealthcheck)
	e.POST("/messages", a.handlePostMessage)
	e.GET("/stream", a.handleStream)
	e.GET("/ws", a.handleWebsocket)
	if a.Config.AuthEnabled {
		e.Use(auth.Middleware(auth.JWTConfig{
			SigningKey: []byte(a.Config.AuthSecret),
		}))
	}
	go func() {
		if err := e.Start(cfg.ServerAddr); err != nil {
			log.Fatal("Server exited:", err)
		}
	}()
	return a
}

func (a *API) handleWebsocketMessage(message config.Message, s *melody.Session) {
	a.RWMutex.Lock()
	a.Messages.PushBack(message)
	a.RWMutex.Unlock()
	a.mrouter.Broadcast(s, message)
}

