	var message config.Message
	if err := c.Bind(&message); err != nil {
		return err
	}
	a.RWMutex.Lock()
	a.Messages.PushBack(message)
	a.RWMutex.Unlock()
	return c.JSON(http.StatusOK, map[string]string{"status": "message_received"})
}

