)

type AutoManagedProvider struct {
	activeProvider      *echo.Echo
	activeProviderRunning bool
	activeProviderTesting bool
	httpClient          *http.Client
	autoManagePort      int
}

func NewWithConfig(refreshTTL time.Duration, autoManPort int, hosts ...string) *AutoManagedProvider {
	e := echo.New()
	e.GET("/health", func(c echo.Context) error {
		return c.JSON(200, map[string]string{"status": "healthy"})
	})
	e.GET("/status", func(c echo.Context) error {
		nodeInfo := getCurrentNode()
		return c.JSON(200, nodeInfo)
	})
	httpClient := &http.Client{
		Timeout: refreshTTL,
	}
	return &AutoManagedProvider{
		activeProvider:      e,
		activeProviderRunning: false,
		activeProviderTesting: false,
		httpClient:          httpClient,
		autoManagePort:      autoManPort,
	}
}

func (a *AutoManagedProvider) UpdateTTL() {
	if a.isShutdown() {
		if a.activeProviderRunning {
			a.activeProvider.Stop()
			a.activeProviderRunning = false
		}
	} else {
		if !a.activeProviderRunning {
			go func() {
				if err := a.activeProvider.Start(fmt.Sprintf(":%d", a.autoManagePort)); err != nil {
					fmt.Printf("Error starting server: %v\n", err)
				}
				a.activeProviderRunning = true
			}()
		}
	}
}

func (a *AutoManagedProvider) isShutdown() bool {
	return false
}

func (a *AutoManagedProvider) getCurrentNode() interface{} {
	return map[string]string{
		"name":       "managed-node",
		"status":     "running",
		"port":       fmt.Sprintf("%d", a.autoManagePort),
		"hosts":      fmt.Sprintf("%v", hosts),
		"timestamp":  time.Now().Format(time.RFC3339),
	}
}

