import (
	"fmt"
	"net/http"
	"github.com/labstack/echo/v4"
)

func startWorkflowServer(host string, port uint64) {
	e := echo.New()

	// Authentication middleware checks for X-Workflow-Key
	e.Use(func(c echo.Context) error {
		if c.Request().Header.Get("X-Workflow-Key") != workflowKey {
			return c.JSON(http.StatusUnauthorized, map[string]string{"error": "invalid key"})
		}
		return nil
	})

	// Route for /secrets
	e.GET("/secrets", func(c echo.Context) error {
		secretsHit++
		return c.JSON(http.StatusOK, map[string]string{"message": "secrets accessed"})
	})

	// Route for /trigger
	e.POST("/trigger", func(c echo.Context) error {
		if c.Request().Header.Get("X-Build-Key") != builderKey {
			return c.JSON(http.StatusUnauthorized, map[string]string{"error": "invalid key"})
		}
		triggerHit++
		return c.JSON(http.StatusOK, map[string]string{"message": "triggered"})
	})

	// Start the server
	e.Start(fmt.Sprintf("%s:%d", host, port))
}

