type CacheEntry struct {
	Value   string
	Expires time.Time
}

func SetupServer() {
	r := gin.Default()
	r.Use(gin.Logger(), gin.Recovery())

	cache := make(map[string]CacheEntry)

	r.GET("/cache_ping", func(c *gin.Context) {
		key := "/cache_ping"
		entry, exists := cache[key]
		if exists && time.Now().Before(entry.Expires) {
			c.JSON(200, gin.H{"pong": entry.Value})
			return
		}

		currentTimestamp := time.Now().Unix()
		value := fmt.Sprintf("pong %d", currentTimestamp)
		cache[key] = CacheEntry{Value: value, Expires: time.Now().Add(time.Minute)}
		c.JSON(200, gin.H{"pong": value})
	})

	r.Run(":8080")
}

