package main

import (
	"os"
	"path"
	"github.com/gin-gonic/gin"
)

func setupServer() {
	r := gin.Default()

	// Serve static files from the public directory
	r.Use(gin.Static("public"))

	// Handle file upload
	r.POST("/upload", func(c *gin.Context) {
		// Parse multipart form
		err := c.Request.ParseMultipartForm(10 << 20) // 10MB
		if err != nil {
			c.JSON(400, gin.H{"error": "failed to parse form"})
			return
		}

		// Get form fields
		name := c.Form("name")
		email := c.Form("email")

		// Validate form fields
		if name == "" || email == "" {
			c.JSON(400, gin.H{"error": "missing name or email"})
			return
		}

		// Get uploaded files
		files, ok := c.Files["files"]
		if !ok || len(files) == 0 {
			c.JSON(400, gin.H{"error": "no files uploaded"})
			return
		}

		// Create upload directory
		uploadDir := "uploads"
		os.MkdirAll(uploadDir, os.ModePerm)

		// Save each file
		for _, file := range files {
			dst, err := os.Create(path.Join(uploadDir, file.Filename))
			if err != nil {
				c.JSON(400, gin.H{"error": "failed to save file: " + err.Error()})
				return
			}
			defer dst.Close()
			if err := file.Save(dst); err != nil {
				c.JSON(400, gin.H{"error": "failed to save file: " + err.Error()})
				return
			}
		}

		// Return success response
		c.JSON(200, gin.H{
			"message": "Upload successful",
			"name":    name,
			"email":   email,
		})
	})

	// Start the server
	r.Run(":8080")
}

