	r.GET("/stream", func(c *gin.Context) {
		c.Writer.Header().Set("Content-Type", "text/event-stream")
		closeChan := c.Writer.CloseNotify()
		for {
			data := "data: " + time.Now().String() + "\n\n"
			c.Writer.Write([]byte(data))
			c.Writer.Flush()
			time.Sleep(1 * time.Second)
			select {
			case <-closeChan:
				break
			default:
				continue
			}
		}
	})
	r.Run(":8080")
}

