	log.Printf("Rate limit set to %d RPS", rps)
	r := gin.Default()
	r.Use(rateLimitMiddleware(rps))
	r.GET("/rate", func(c *gin.Context) {
		c.JSON(200, gin.H{"message": "Rate limited endpoint"})
