	"github.com/gin-gonic/gin"
	"path/filepath"
)

type SuccessResponse struct {
	Status int      `json:"status"`
	Message string   `json:"message"`
	Data struct {
		URL string `json:"url"`
	} `json:"data"`
}

type ErrorResponse struct {
	Status int      `json:"status"`
	Message string   `json:"message"`
}

func randString(n int) string {
	const letters = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789"
	b := make([]byte, n)
	if _, err := io.ReadFull(rand.Reader, b); err != nil {
		return ""
	}
	for i := range b {
		b[i] = letters[b[i]%len(letters)]
	}
	return string(b)
}

func copyFile(src, dst *os.File) error {
	_, err := io.Copy(dst, src)
	return err
}

func main() {
	r := gin.Default()

	// Serve static files from /root/file/
	r.Static("/file", "/root/file/")

	// Upload endpoint
	r.POST("/upload", func(c *gin.Context) {
		// Parse the multipart form
		if err := c.Request.ParseMultipartForm(10 << 20); err != nil {
			c.JSON(500, ErrorResponse{Status: 1, Message: "Error parsing form"})
			return
		}

		// Check if the "file" field exists
		file, err := c.FormFile("file")
		if err != nil {
			c.JSON(400, ErrorResponse{Status: 1, Message: "No file uploaded"})
			return
		}
		defer file.Close()

		// Generate unique filename
		originalFilename := file.Filename
		ext := filepath.Ext(originalFilename)
		baseName := strings.TrimSuffix(originalFilename, ext)
		uniqueName := fmt.Sprintf("%d-%s%s", time.Now().UnixNano(), randString(10), ext)

		// Define the destination path
		dest := "/root/file/" + uniqueName

		// Ensure the directory exists
		os.MkdirAll(filepath.Dir(dest), 0755)

		// Open the destination file
		out, err := os.Create(dest)
		if err != nil {
			c.JSON(500, ErrorResponse{Status: 1, Message: "Could not create file"})
			return
		}
		defer out.Close()

		// Copy the uploaded file to the destination
		if err := copyFile(file, out); err != nil {
			c.JSON(500, ErrorResponse{Status: 1, Message: "Error copying file"})
			return
		}

		// Construct the URL
		url := "http://localhost:8085/file/" + uniqueName

		// Return success response
		c.JSON(0, SuccessResponse{
			Status: 0,
			Message: "File uploaded successfully",
			Data: struct {
				URL string `json:"url"`
			}{
				URL: url,
			},
		})
	})

	// Start the server
	r.Run(":8085")
}

