package main

import (
	"github.com/gin-gonic/gin"
	"github.com/dgrijalva/jwt-go"
	"net/http"
	"time"
)

var jwtSecret = []byte("your-secure-secret-key")

type Claims struct {
	UserID int `json:"user_id"`
	jwt.StandardClaims
}

func generateToken(userID int) (string, error) {
	claims := &Claims{
		UserID: userID,
		StandardClaims: jwt.StandardClaims{
			ExpiresAt: time.Now().Add(24 * time.Hour).Unix(),
		},
	}
	token, err := jwt.NewWithClaims(jwt.SigningMethodHS256, claims).SignedString(jwtSecret)
	if err != nil {
		return "", err
	}
	return token, nil
}

func authMiddleware() gin.HandlerFunc {
	return func(c *gin.Context) {
		tokenString := c.GetHeader("Authorization")
		if tokenString == "" {
			c.AbortWithStatusJSON(http.StatusUnauthorized, gin.H{"error": "missing token"})
			return
		}
		token, err := jwt.ParseWithClaims(tokenString, &Claims{}, func(token *jwt.Token) (interface{}, error) {
			return jwtSecret, nil
		})
		if err != nil {
			c.AbortWithStatusJSON(http.StatusUnauthorized, gin.H{"error": "invalid token"})
			return
		}
		if _, ok := token.Claims.(*Claims); !ok || !token.Valid {
			c.AbortWithStatusJSON(http.StatusUnauthorized, gin.H{"error": "invalid token"})
			return
		}
		c.Set("userID", token.Claims.(*Claims).UserID)
		c.Next()
	}
}

func main() {
	r := gin.Default()

	publicGroup := r.Group("/api/")
	publicGroup.GET("/", func(c *gin.Context) {
		token, _ := generateToken(1)
		c.JSON(http.StatusOK, gin.H{"token": token})
	})

	privateGroup := r.Group("/api/private/")
	privateGroup.Use(authMiddleware())
	privateGroup.GET("/", func(c *gin.Context) {
		userID := c.MustGet("userID").(int)
		c.JSON(http.StatusOK, gin.H{"message": "Private message for user ID: " + strconv.Itoa(userID)})
	})

	r.Run(":8080")
}

