	Token    string
}

func initConfig() Config {
	// Dummy config initialization
	return Config{
		IP:       "0.0.0.0",
		Port:     8080,
		Proxy:    os.Getenv("PROXY"),
		Token:    "your-deepl-token",
	}
}

func main() {
	config := initConfig()
	fmt.Printf("Server initializing with IP: %s, Port: %d, Proxy: %s, Token: %s\n", config.IP, config.Port, config.Proxy, config.Token)

	r := gin.Default()

	// CORS middleware
	corsConfig := cors.New(cors.Config{
		AllowAllOrigins: true,
		AllowMethods:    []string{"GET", "POST"},
		AllowHeaders:    []string{"Content-Type", "Authorization"},
	})
	r.Use(corsConfig)

	// Authentication middleware
	authMiddleware := func(c *gin.Context) {
		tokenHeader := c.GetHeader("Authorization")
		if tokenHeader != config.Token {
			c.AbortWithStatusJSON(http.StatusUnauthorized, gin.H{"error": "Invalid token"})
			return
		}
		c.Next()
	}

	// Root endpoint
	r.GET("/", func(c *gin.Context) {
		c.JSON(200, gin.H{
			"message": "Server is running",
			"ip":      config.IP,
			"port":    config.Port,
		})
	})

	// Translate endpoint
	r.POST("/translate", authMiddleware, func(c *gin.Context) {
		var req struct {
			Text        string `json:"text"`
			SourceLang  string `json:"source_lang"`
			TargetLang  string `json:"target_lang"`
			// Add more fields as needed
		}
		if err := c.BindJSON(&req); err != nil {
			c.JSON(400, gin.H{"error": "Invalid request body"})
			return
		}

		result, err := translateText(req.Text, req.SourceLang, req.TargetLang, config.Proxy, config.Token)
		if err != nil {
			c.JSON(500, gin.H{"error": err.Error()})
			return
		}

		c.JSON(200, gin.H{"translation": result})
	})

	// Start server
	fmt.Printf("Server starting on %s:%d\n", config.IP, config.Port)
	r.Run(fmt.Sprintf("%s:%d", config.IP, config.Port))
}

func translateText(text, sourceLang, targetLang, proxyURL, token string) (string, error) {
	client := &http.Client{}
	if proxyURL != "" {
		proxy, _ := url.Parse(proxyURL)
		client.Transport = &http.Transport{
			Proxy: http.ProxyURL(proxy),
		}
	}

	url := "https://api.deepl.com/v2/translate"
	params := url.Values{
		"text":        {text},
		"source_lang": {sourceLang},
		"target_lang": {targetLang},
		"auth_key":    {token},
	}
	fullURL := url + "?" + params.Encode()

	resp, err := client.Get(fullURL)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	body, _ := io.ReadAll(resp.Body)
	var result map[string]interface{}
	json.Unmarshal(body, &result)

	if translations, ok := result["translations"].([]interface{}); ok && len(translations) > 0 {
		return translations[0].(map[string]interface{})["text"].(string), nil
	}

	return "", fmt.Errorf("no translation found: %v", result)
}

