func Tcp(ctx context.Context, req *TcpRequest, rsp *TcpResponse) error {
	if req.Address == "" {
		return fmt.Errorf("address is required")
	}
	parts := strings.SplitN(req.Address, ":", 2)
	if len(parts) != 2 {
		return fmt.Errorf("invalid address format")
	}
	host, port := parts[0], parts[1]
	if _, err := strconv.Atoi(port); err != nil {
		return fmt.Errorf("invalid port number: %v", err)
	}

	addr, err := net.ResolveTCPAddr("tcp", req.Address)
	if err != nil {
		return err
	}

	deadline, ok := ctx.Deadline()
	var timeout time.Duration
	if !ok {
		timeout = 5 * time.Second
	} else {
		timeout = time.Until(deadline)
	}

	conn, err := net.DialTimeout("tcp", req.Address, timeout)
	if err != nil {
		rsp.Status = err.Error()
		rsp.Data = ""
		return nil
	}
	defer conn.Close()

	if req.Data != "" {
		_, err := conn.Write([]byte(req.Data))
		if err != nil {
			rsp.Status = err.Error()
			rsp.Data = ""
			return nil
		}
	}

	reader := bufio.NewReader(conn)
	data, err := reader.ReadString('\n')
	if err != nil {
		rsp.Status = err.Error()
		rsp.Data = ""
		return nil
	}

	rsp.Status = "OK"
	rsp.Data = data

	return nil
}

