type DNSEndpoint struct {
	Addr string
}

func (e *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (n int, err error) {
	parts := strings.Split(e.Addr, ":")
	if len(parts) != 2 {
		return 0, errors.New("invalid address format")
	}
	host, port := parts[0], parts[1]

	udpAddr, err := net.ResolveUDPAddr("udp", e.Addr)
	if err != nil {
		return 0, err
	}

	conn, err := net.DialUDP("udp", nil, udpAddr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if ctx != nil {
		deadline, ok := ctx.Deadline()
		if ok {
			conn.SetDeadline(deadline)
		}
	}

	txId := make([]byte, 2)
	_, err = rand.Read(txId)
	if err != nil {
		return 0, err
	}

	query := append(txId, payload...)

	n, err = conn.Write(query)
	if err != nil {
		return n, err
	}

	n, err = conn.Read(buf)
	if err != nil {
		return n, err
	}

	if len(buf) < 2 {
		return n, errors.New("invalid response")
	}
	receivedTxId := buf[0:2]
	if !bytes.Equal(receivedTxId, txId) {
		return n, errors.New("transaction ID mismatch")
	}

	return n, nil
}

