func (c *OpenTSDBConfig) OpenTSDBWithConfig() {
	go func() {
		ticker := time.NewTicker(c.FlushInterval)
		for range ticker.C {
			if err := c.openTSDB(); err != nil {
				log.Println("Error sending metrics:", err)
			}
		}
	}()
}

func (c *OpenTSDBConfig) openTSDB() error {
	conn, err := net.Dial("tcp", c.Addr.String())
	if err != nil {
		return err
	}
	defer conn.Close()

	for _, m := range c.Registry.MetricIterator() {
		metric := m.(prometheus.Collector)
		name := c.Prefix + "." + metric.GetName()
		var value float64
		switch metric.GetType() {
		case prometheus.Counter_Type:
			value = metric.GetCounter().Value()
		case prometheus.Gauge_Type:
			value = metric.GetGauge().Get()
		case prometheus.Histogram_Type:
			value = metric.GetHistogram().GetSnapshot().Sum()
		case prometheus.Meter_Type:
			value = metric.GetMeter().Rate(1)
		case prometheus.Timer_Type:
			duration := metric.GetTimer().Mean()
			value = duration.Seconds() / float64(c.DurationUnit.Seconds())
		default:
			value = 0
		}
		timestamp := time.Now().UnixNano() / 1e6 // milliseconds
		line := fmt.Sprintf("%s %v %d\n", name, value, timestamp)
		_, err := conn.Write([]byte(line))
		if err != nil {
			return err
		}
	}
	return nil
}

