type request struct {
	Method   string
	Path     string
	Version  string
	Headers  map[string]string
	Body     []byte
	Conn     *net.Conn
}

type proxy struct{}

func (p *proxy) Connect(conn *net.Conn) {}

func (h *HTTP) ListenAndServe() {
	listener, err := net.Listen("tcp", h.addr)
	if err != nil {
		log.Fatal(err)
	}
	defer listener.Close()
	for {
		conn, err := listener.Accept()
		if err != nil {
			log.Println("Error accepting connection:", err)
			continue
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	conn.SetKeepAlive(true)
	conn.SetDeadline(time.Now().Add(30 * time.Second))
	req, _ := h.parseRequest(conn)
	h.servRequest(req, conn)
}

func (h *HTTP) parseRequest(conn net.Conn) (*request, *net.Conn) {
	reader := bufio.NewReader(conn)
	line, _, err := reader.ReadLine()
	if err != nil {
		return nil, nil
	}
	method, path, version := parseRequestLine(line)
	headers := make(map[string]string)
	for {
		line, _, err := reader.ReadLine()
		if err != nil || string(line) == "\r\n" {
			break
		}
		parts := strings.SplitN(string(line), ": ", 2)
		if len(parts) == 2 {
			headers[parts[0]] = parts[1]
		}
	}
	body, _, err := reader.ReadAll(1024)
	if err != nil {
		body = []byte{}
	}
	return &request{
		Method:   method,
		Path:     path,
		Version:  version,
		Headers:  headers,
		Body:     body,
		Conn:     &conn,
	}, &net.Conn{}
}

func (h *HTTP) servRequest(req *request, conn *net.Conn) {
	if h.user != "" && h.password != "" {
		authHeader := req.Headers["Authorization"]
		if authHeader == "" {
			h.sendAuth(req, "Basic realm=\"Proxy Auth\"")
			return
		}
		decoded, _ := base64.StdEncoding.DecodeString(authHeader[len("Basic "):])
		username, password, _ := strings.Credentials(decoded)
		if username != h.user || password != h.password {
			h.sendAuth(req, "Basic realm=\"Proxy Auth\"")
			return
		}
	}
	if req.Method == "CONNECT" {
		h.servHTTPS(req, conn)
	} else {
		h.servHTTP(req, conn)
	}
}

func (h *HTTP) servHTTPS(req *request, conn *net.Conn) {
	targetHost := req.Headers["Host"]
	parts := strings.SplitN(targetHost, ":", 2)
	var targetPort int
	if len(parts) == 1 {
		targetPort = 443
	} else {
		var err error
		targetPort, err = strconv.Atoi(parts[1])
		if err != nil {
			targetPort = 443
		}
	}
	serverConn, _ := net.Dial("tcp", fmt.Sprintf("%s:%d", parts[0], targetPort))
	go func() {
		io.Copy(serverConn, *conn)
	}()
	go func() {
		io.Copy(*conn, serverConn)
	}()
}

func (h *HTTP) servHTTP(req *request, conn *net.Conn) {
	host := req.Headers["Host"]
	parts := strings.SplitN(host, ":", 2)
	targetHost := parts[0]
	var targetPort int
	if len(parts) == 1 {
		targetPort = 80
	} else {
		var err error
		targetPort, err = strconv.Atoi(parts[1])
		if err != nil {
			targetPort = 80
		}
	}
	serverConn, _ := net.Dial("tcp", fmt.Sprintf("%s:%d", targetHost, targetPort))
	if h.pretend {
		_, _ = *conn.Write([]byte("HTTP/1.1 404 Not Found\r\nContent-Length: 0\r\n\r\n"))
		return
	}
	for key, value := range req.Headers {
		_, _ = serverConn.Write([]byte(fmt.Sprintf("%s: %s\r\n", key, value)))
	}
	_, _ = serverConn.Write([]byte("\r\n"))
	_, _ = serverConn.Write(req.Body)
	response, _ := io.ReadAll(serverConn)
	_, _ = *conn.Write(response)
}

func (h *HTTP) sendAuth(req *request, realm string) {
	_, _ = req.Conn.Write([]byte(fmt.Sprintf("HTTP/1.1 401 Unauthorized\r\nWWW-Authenticate: %s\r\nContent-Length: 0\r\n\r\n", realm)))
}

func parseRequestLine(line []byte) (string, string, string) {
	parts := strings.SplitN(string(line), " ", 3)
	if len(parts) < 3 {
		return "", "", ""
	}
	return parts[0], parts[1], parts[2]
}

